export type VFile = import('vfile').VFile
export type Root = import('mdast').Root
export type Content = import('mdast').Content
export type Resource = Extract<Root | Content, import('mdast').Resource>
export type Reference = {
  filePath: string
  hash: string
}
export type ReferenceMap = {
  file: VFile
  reference: Reference
  nodes: Resource[]
}
export type Landmarks = Record<string, Record<string, boolean>>
export type References = Record<string, Record<string, ReferenceMap[]>>
